import React, { useEffect, useState } from 'react';
import {View, Text, TextInput, Button, StyleSheet, TouchableOpacity, Alert, Linking} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';

export default function Login() {

  const navigation = useNavigation();

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const checkToken = async () => {
      const token = await AsyncStorage.getItem('auth_token');
      if (token) {
        navigation.navigate('Home');
      }
    };
    checkToken();

    // Add listener for OAuth redirect URL
    const handleUrl = async (event) => {
      const url = event.url;
      // Parse authorization code from URL query parameters
      const codeMatch = url.match(/[?&]code=([^&]+)/);
      if (codeMatch) {
        const authCode = codeMatch[1];
        try {
          // Exchange authorization code with backend
          const response = await fetch('http://127.0.0.1:8000/google/login/', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ code: authCode }),
          });
          if (response.ok) {
            const data = await response.json();
            await AsyncStorage.setItem('auth_token', JSON.stringify(data));
            navigation.navigate('Home');
          } else {
            const errData = await response.json();
            setError(errData.detail || 'Google login failed');
          }
        } catch (err) {
          setError('Google login failed');
        }
      }
    };

    Linking.addEventListener('url', handleUrl);

    // Cleanup listener on unmount
    return () => {
      Linking.removeEventListener('url', handleUrl);
    };
  }, []);

  const loginWithGoogle = () => {
    Linking.openURL('https://accounts.google.com/o/oauth2/v2/auth?redirect_uri=http://localhost:19006&prompt=consent&response_type=code&client_id=642552962636-sva3lieul9tqmi3ag3g4kghba1a8kfjr.apps.googleusercontent.com&scope=openid%20email%20profile');
  };

  const handleSubmit = async () => {
    setError('');
    try {
      const response = await fetch('http://127.0.0.1:8000/formlogin/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        const data = await response.json();
        await AsyncStorage.setItem('auth_token', JSON.stringify(data));
        navigation.navigate('Home');
      } else {
        const errData = await response.json();
        setError(errData.detail || 'Login failed');
      }
    } catch (err) {
      setError('Login failed');
    }
  };

  const goToRegister = () => {
    navigation.navigate('Signup');
  };

  return (
    <View style={styles.container}>
      <Text style={styles.label}>Email:</Text>
      <TextInput
        style={styles.input}
        value={email}
        onChangeText={setEmail}
        keyboardType="email-address"
        autoCapitalize="none"
      />
      <Text style={styles.label}>Password:</Text>
      <TextInput
        style={styles.input}
        value={password}
        onChangeText={setPassword}
        secureTextEntry
      />
      {error ? <Text style={styles.error}>{error}</Text> : null}
      <Button title="Sign in" onPress={handleSubmit} />
      <TouchableOpacity style={styles.googleButton} onPress={loginWithGoogle}>
        <Text style={styles.googleButtonText}>Sign in with Google</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={goToRegister}>
        <Text style={styles.registerText}>Register</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    padding: 20,
    flex: 1,
    justifyContent: 'center',
    backgroundColor: '#fff'
  },
  label: {
    marginBottom: 4,
    fontWeight: 'bold'
  },
  input: {
    borderWidth: 1,
    borderColor: '#999',
    padding: 8,
    marginBottom: 10,
    borderRadius: 4
  },
  error: {
    color: 'red',
    marginBottom: 10
  },
  googleButton: {
    backgroundColor: '#4285F4',
    padding: 12,
    borderRadius: 4,
    marginTop: 10
  },
  googleButtonText: {
    color: 'white',
    textAlign: 'center',
    fontWeight: 'bold'
  },
  registerText: {
    color: 'blue',
    marginTop: 12,
    textAlign: 'center'
  }
});
